/*---------------------------------
  RESPONSIVE NAVIGATION
  ---------------------------------*/
function setupMenus() {
    jQuery('.primary-navigation').each(function() {
        var mobileMenu = jQuery(this).clone();
        var mobileMenuWrap = jQuery('<div></div>').append(mobileMenu);
        mobileMenuWrap.attr('class', "open-close-wrapper");
        jQuery(this).parent().append(mobileMenuWrap);
        mobileMenu.attr('class', 'menu-mobile');
    });
}
function setupMobileMenu() {
    jQuery(".menu-wrap").each(function() {
        var clickTopOpenMenu = jQuery(this).find(".click-to-open-menu");
        clickTopOpenMenu.click(function() {
            jQuery(this).parent().find('.open-close-wrapper').slideToggle("fast");
        });
    });
}


jQuery(document).ready(function($){
	/*---------------------------------
	  NAVIGATION
	  ---------------------------------*/
	setupMenus();
	setupMobileMenu();	
	$(".primary-navigation").superfish({cssArrows:true});



	/*---------------------------------
	  CAROUSEL
	  ---------------------------------*/
	$("#featured-carousel").bxSlider({
		controls: true,
		adaptiveHeight: true,
		prevText: "&#xf053;",
		nextText: "&#xf054;",
		pager: false,
		minSlides: 1,
		maxSlides: 4,
		slideWidth: 276,
		slideMargin: 30
	});
	/*---------------------------------
	  FITVIDS
	  ---------------------------------*/
	$(".container, #featured-slider").fitVids();
	/*---------------------------------
	  TABS
	  ---------------------------------*/
	$(".tab-container").easytabs({ animationSpeed: "fast" });
	/*---------------------------------
	  TOGGLES
	  ---------------------------------*/
	$(".toggle-box").hide();
		$("a.toggle-title").click(function(){
		$(this).toggleClass("active").next(".toggle-box").slideToggle("fast");
			return true;
	});
	/*---------------------------------
	  LIGHTBOX
	  ---------------------------------*/
	$("a[rel^='lightbox']").prettyPhoto({
		theme: "pp_default",
		social_tools: false
	});
	/*---------------------------------
	  BACK TO TOP
	  ---------------------------------*/
	$("#back-to-top").click(function () {
		$("body,html").animate({
			scrollTop: 0
		}, 800);
		return false;
	});



});



